package ConditionalStatements;

public class Switch_Case_Demo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int month = 125;

		switch(month)
		{
		case 01:
		System.out.println("the entered month is janauary");
		break;

		case 02:
		System.out.println("the entered month is febraury");
		break;

		case 03:
		System.out.println("the entered month is march");
		break;


		case 04:
		System.out.println("the entered month is april");
		break;


		default:
		System.out.println("the enetred input is not correct, please recheck and reenter ");
		}
		
		
	}

}
